    let current_date = new Date();
    this.updated_at = current_date;

    if (!this.created_at) {
      this.created_at = current_date;
    }

    if (this.password) {
      // TODO password hashed
    }

    next();
  })
  .post('save', function (error, doc, next) {
    if (error.name !== 'MongoError') {
      return next();
    }

    next(error);
  })
);

